hibernate-mapping.query:
select count(*) from org.mifos.customers.business.CustomerBO as c left join c.parentCustomer as pa left join pa.parentCustomer as papa left join c.office as off left join c.personnel as per where ( (:USERLEVEL_ID=:LOID and c.personnel.personnelId=:USERID) or :USERLEVEL_ID!=:LOID) and c.office.officeId=:OFFICEID and ((c.customerLevel.id !=:LEVELID and c.displayName like :SEARCH_STRING) or (c.customerLevel.id =:LEVELID
and (c.firstName like :SEARCH_STRING or c.lastName like :SEARCH_STRING or c.secondLastName like :SEARCH_STRING or c.displayName like :SEARCH_STRING or (c.firstName like :SEARCH_STRING1 and c.lastName like :SEARCH_STRING2 )
)
) )
