changeSet.sql:
drop table if exists temporary_account_ids; create table temporary_account_ids (account_id INT(11));
insert into temporary_account_ids (account_id) select a.account_id from account a inner join (select ls6.account_id, sum(ls6.interest) as interestsum, sum(ls6.principal) as principalsum from loan_schedule ls6 group by ls6.account_id) as ls5 on a.account_id=ls5.account_id inner join loan_summary ls on ls.account_id=a.account_id where ls.orig_principal-ls5.principalsum > 1 and a.account_state_id in (5,9);
update loan_schedule as target inner join (select lsu1.orig_principal, lsu1.orig_interest, lsu1.account_id from loan_summary lsu1) as ls2 on target.account_id=ls2.account_id inner join (select ls6.account_id, max(ls6.installment_id) as instno, sum(ls6.interest) as interestsum, sum(ls6.principal) as principalsum from loan_schedule ls6 group by ls6.account_id) as ls5 on target.account_id=ls5.account_id set target.interest=target.interest+(ls2.orig_interest-ls5.interestsum), target.principal=target.principal+(ls2.orig_principal-ls5.principalsum) where target.installment_id=ls5.instno and target.account_id in ( select * from temporary_account_ids );
update loan_schedule as target inner join temporary_account_ids tai on target.account_id = tai.account_id set target.principal_paid=target.principal_paid+(target.interest_paid-target.interest), target.interest_paid=target.interest where target.interest_paid > target.interest;
drop table if exists temporary_account_ids;

